class HomeController < ApplicationController
  def index
    values = RouteDelay.on_week_days.avg_global_delay.map{|t, d| [t.seconds_since_midnight.round, d.round]}
    @global = (0..144).map{|t| [t*600, nil]}.to_h.merge(values.to_h).to_a
  end

